import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router, RouterLink } from '@angular/router';
import { ApiService } from '../../services/api.service';
import { Complaint } from '../../models/complaint.model';

@Component({
  selector: 'app-complaint-list',
  standalone: true,
  imports: [CommonModule, RouterLink],
  templateUrl: './complaint-list.component.html'
})
export class ComplaintListComponent implements OnInit {
  complaints: Complaint[] = [];
  userName = localStorage.getItem('userName') || '';
  constructor(private api: ApiService, private router: Router) {}
  ngOnInit() { this.api.myComplaints().subscribe(r => this.complaints = r); }
  open(c: Complaint){ this.router.navigate(['/complaints', c._id]); }
}
<h2>Welcome{{ userName ? ', ' + userName : '' }} ðŸ‘‹</h2>
<h3>My Complaints</h3>

<a class="btn" routerLink="/new">+ New complaint</a>

<div *ngFor="let c of complaints" class="card" (click)="open(c)" style="cursor:pointer">
  <h3>{{c.title}} â€” <span class="status {{c.status}}">{{c.status}}</span></h3>
  <p>{{c.description}}</p>
  <img *ngIf="c.photo" [src]="'/uploads/' + c.photo" width="220" style="border-radius:10px"/>
  <small>Filed: {{c.createdAt | date:'medium'}}</small>
</div>
